package main

import (
	"strconv"

	"github.com/gin-gonic/gin"
)

func main() {

	flag := `NISRA{}`

	r := gin.Default()

	r.GET("/id", func(c *gin.Context) {

		nstr := c.Query("n")

		if len(nstr) == 0 {
			c.Redirect(302, "/next?id=0")
		}

		n, err := strconv.Atoi(nstr)

		if err != nil || n < 0 {
			n = 0
		}

		n = n % len(flag)

		c.Header(flag[n:n+1], "I dont want to eat pineapple pizza QQ!")

		c.String(200, "Nothing here!")

	})

	r.GET("/", func(c *gin.Context) {

		c.Redirect(302, "/id?n=0")

	})

	r.Run() // listen and serve on 0.0.0.0:8080
}
